mapper.select:
select
  *
from
  confirmation_of_rights
where
  project_id = #{projectId}
and `year` = #{year}
order by `quarter` desc limit 1;

SELECT
  c.*,p.name AS projectName
FROM
  confirmation_of_rights c
    LEFT JOIN
  project p ON c.project_id = p.id
WHERE 1=1

  AND c.project_id = #{projectId}


  AND c.name LIKE concat('%',#{projectName},'%')


  AND c.year = #{year}


  AND c.quarter = #{quarter}

ORDER BY c.id DESC

  limit #{offset},#{length}

SELECT COUNT(*) FROM confirmation_of_rights c LEFT JOIN project p ON c.project_id = p.id WHERE 1=1
AND c.project_id = #{projectId}
AND c.name LIKE concat('%',#{projectName},'%')
AND c.year = #{year}
AND c.quarter = #{quarter}
