mapper.select:
SELECT ic.*,p.name AS projectName,cu.name AS createUserStr,uu.name AS updateUserStr,ac.team_name AS teamName, s.name AS subcontractorName,sum(ac2.estimated_contract_amount) AS sumContractAmount FROM inspection_account ic LEFT JOIN project p ON ic.project_id = p.id LEFT JOIN `user` cu ON ic.create_user = cu.id LEFT JOIN `user` uu ON ic.update_user = uu.id LEFT JOIN labor_account ac ON ic.labor_account_id = ac.id LEFT JOIN subcontractor s ON s.id = ic.subcontractor_id LEFT JOIN labor_account ac2 on ac.team_name = ac2.team_name AND ac2.project_id = ic.project_id and ac2.subcontractor_id = ic.subcontractor_id WHERE 1=1 AND ic.deleted != 1
AND ac.team_name LIKE concat('%',#{teamName},'%')
AND ic.project_id = #{projectId}
AND p.name LIKE concat('%',#{projectName},'%')
AND s.name LIKE concat('%',#{subcontractorName},'%')
AND ic.valuation_type = #{valuationType}
AND date_format(ic.valuation_time,'%Y-%m') = #{valuationTime}
AND under_rate >= #{minUnderRate} AND under_rate <= #{maxUnderRate}
GROUP BY ac2.team_name,ic.project_id,ic.subcontractor_id,ic.valuation_period ORDER BY ic.id DESC
limit #{offset},#{length}

SELECT COUNT(*) FROM inspection_account ic LEFT JOIN project p ON ic.project_id = p.id LEFT JOIN subcontractor s ON ic.subcontractor_id = s.id LEFT JOIN labor_account ac ON ic.labor_account_id = ac.id WHERE 1=1 AND ic.deleted != 1
AND ac.team_name LIKE concat('%',#{teamName},'%')
AND ic.project_id = #{projectId}
AND p.name LIKE concat('%',#{projectName},'%')
AND s.name LIKE concat('%',#{subcontractorName},'%')
AND ic.valuation_type = #{valuationType}
AND date_format(ic.valuation_time,'%Y-%m') = #{valuationTime}
AND under_rate >= #{minUnderRate} AND under_rate <= #{maxUnderRate}

select
  SUM(valuation_price)
from
  inspection_account
where deleted != 1 AND
  project_id = #{projectId}
  and subcontractor_id = #{subcontractorId}
  and labor_account_id = #{laborAccountId};

select
  *
from
  inspection_account
where deleted != 1 AND labor_account_id = #{laborAccountId} AND project_id = #{projectId} AND subcontractor_id = #{subcontractorId}

  AND valuation_type = #{valuationType}

SELECT
  ic.*
FROM
  inspection_account ic
LEFT JOIN
  labor_account lc ON ic.labor_account_id = lc.id
WHERE
  ic.project_id = #{projectId}
  AND ic.subcontractor_id = #{subcontractorId}
  AND lc.team_name = #{teamName};

SELECT
  *
FROM
  inspection_account
WHERE
  project_id = #{projectId} AND subcontractor_id = #{subcontractorId}
    AND labor_account_id = #{laborAccountId}
    AND valuation_period = #{valuationPeriod}
