mapper.select:
SELECT ic.*,p.name AS projectName,cu.name AS createUserStr,uu.name AS updateUserStr FROM inspection_account ic LEFT JOIN project p ON ic.project_id = p.id LEFT JOIN `user` cu ON ic.create_user = cu.id LEFT JOIN `user` uu ON ic.update_user = uu.id WHERE 1=1
AND ic.project_id = #{projectId}
AND p.name LIKE concat('%',#{projectName},'%')
AND ic.subcontractor_name LIKE concat('%',#{subcontractorName},'%')
AND ic.valuation_type = #{valuationType}
AND ic.valuation_time = #{valuationTime}
ORDER BY ic.id DESC
limit #{offset},#{length}

SELECT
 COUNT(*)
FROM
inspection_account ic LEFT JOIN project p ON ic.project_id = p.id
WHERE 1=1

  AND ic.project_id = #{projectId}


  AND p.name LIKE concat('%',#{projectName},'%')


  AND ic.subcontractor_name LIKE concat('%',#{subcontractorName},'%')


  AND ic.valuation_type = #{valuationType}


  AND ic.valuation_time = #{valuationTime}
