mapper.select:
SELECT la.*, p.name AS projectName, cu.name AS createUserStr, uu.name AS updateUserStr,info.contract_number AS contractNumber, s.name AS subcontractorName FROM labor_account la LEFT JOIN project p ON la.project_id = p.id LEFT JOIN project_info info ON info.project_id = la.project_id LEFT JOIN `user` cu ON la.create_user = cu.id LEFT JOIN `user` uu ON la.update_user = uu.id LEFT JOIN subcontractor s ON s.id = la.subcontractor_id WHERE 1=1
AND la.project_id = #{projectId}
AND p.name LIKE CONCAT('%',#{projectName}, '%')
AND la.subcontractor_name LIKE CONCAT('%',#{subcontractorName}, '%')
AND la.status = #{status}
AND la.approval_filing = #{approvalFiling}
ORDER BY la.id DESC
limit #{offset},#{length}

SELECT COUNT(*) FROM labor_account la LEFT JOIN project p ON la.project_id = p.id WHERE 1=1
AND la.project_id = #{projectId}
AND p.name LIKE CONCAT('%',#{projectName}, '%')
AND la.subcontractor_name LIKE CONCAT('%',#{subcontractorName}, '%')
AND la.status = #{status}
AND la.approval_filing = #{approvalFiling}
