mapper.select:
SELECT lc.*, p.name AS projectName, d.value AS projectType, info.status AS projectStatus, info.contract_start_time AS contractStartTime, info.contract_end_time AS contractEndTime FROM liability_cost lc LEFT JOIN project p ON lc.project_id = p.id LEFT JOIN project_info info ON p.id = info.project_id LEFT JOIN dict d ON d.id = p.dict_id WHERE 1 = 1
AND p.name LIKE CONCAT('%', #{projectName}, '%')
AND YEAR(lc.report_time) = #{year}
AND MONTH(lc.report_time) = #{mon}
ORDER BY lc.id DESC
limit #{offset},#{length}
