mapper.select:
SELECT o.*, p.`name` AS projectName, pi.project_type AS projectType, pi.contract_number AS contractNumber, s.`name` AS subcontractorName, la.team_name AS teamName, la.contract_person AS contractPerson FROM out_of_contract_compensation_statistics o LEFT JOIN project_info pi ON o.project_id = pi.project_id LEFT JOIN subcontractor s ON o.subcontractor_id = s.id LEFT JOIN labor_account la ON o.labor_account_id = la.id LEFT JOIN project p ON o.project_id = p.id WHERE 1=1
AND p.`name` LIKE CONCAT('%', #{projectName}, '%')
AND s.`name` LIKE CONCAT('%', #{subcontractorName}, '%')
AND la.team_name LIKE CONCAT('%', #{teamName}, '%')
AND YEAR(o.report_time) = #{year}
AND o.quarter = #{quarter}
AND o.project_id = #{projectId}
ORDER BY id DESC
limit #{offset},#{limit}

SELECT count(*) FROM out_of_contract_compensation_statistics o LEFT JOIN project_info pi ON o.project_id = pi.project_id LEFT JOIN subcontractor s ON o.subcontractor_id = s.id LEFT JOIN labor_account la ON o.labor_account_id = la.id LEFT JOIN project p ON o.project_id = p.id WHERE 1=1
AND p.`name` LIKE CONCAT('%', #{projectName}, '%')
AND s.`name` LIKE CONCAT('%', #{subcontractorName}, '%')
AND la.team_name LIKE CONCAT('%', #{teamName}, '%')
AND YEAR(o.report_time) = #{year}
AND o.quarter = #{quarter}
AND o.project_id = #{projectId}

SELECT o.id, o.project_id, sum(o.daily_work_subtotal) as statisticsDailyWorkSubtotal, sum(o.compensation_subtotal) as statisticsCompensationSubtotal, sum(o.estimate_daily_work_subtotal) as statisticsEstimateDailyWorkSubtotal, sum(o.estimate_compensation_subtotal) as statisticsEstimateCompensationSubtotal, sum(o.total_amount_contract) as statisticsTotalAmountContract, p.`name` AS projectName, pi.project_type AS projectType FROM out_of_contract_compensation_statistics o LEFT JOIN project_info pi ON o.project_id = pi.project_id LEFT JOIN project p ON o.project_id = p.id WHERE 1=1
AND o.project_id = #{projectId}
AND p.`name` LIKE concat('%',#{projectName},'%')
group by o.project_id ORDER BY id DESC
limit #{offset},#{length}
