mapper.select:
SELECT p.*,cu.name AS createUserStr,uu.name AS updateUserStr FROM personnel p LEFT JOIN `user` cu ON p.create_user = p.id LEFT JOIN `user` uu ON p.update_user = uu.id WHERE 1=1
AND p.`name` LIKE concat('%',#{name},'%')
AND p.project_name LIKE concat('%',#{projectName},'%')
AND p.`position` LIKE concat('%',#{position},'%')
AND p.first_degree_level = #{firstDegreeLevel}
AND p.second_degree_level = #{secondDegreeLevel}
AND p.work_time = #{workTime}
ORDER BY p.id DESC
limit #{offset},#{length}

SELECT COUNT(*) FROM personnel WHERE 1=1

  AND `name` LIKE concat('%',#{name},'%')


  AND project_name LIKE concat('%',#{projectName},'%')


  AND `position` LIKE concat('%',#{position},'%')


  AND work_time = #{workTime}


  AND first_degree_level = #{firstDegreeLevel}


  AND second_degree_level = #{secondDegreeLevel}
