mapper.select:
SELECT
  info.*,p.name as projectName,p.code
FROM
  project_info info
LEFT JOIN
  project p ON info.project_id = p.id
WHERE 1=1

    AND p.id = #{projectId}


    AND p.name LIKE concat('%',#{projectName},'%')


    AND info.status = #{status}


    AND info.project_manager = #{projectManager}


    AND info.project_secretary = #{projectSecretary}


    AND info.chief_engineer = #{chiefEngineer}


    AND info.contract_start_time <= #{contractStartTime} AND info.contract_start_time >= #{contractEndTime}


    AND info.real_contract_start_time <= #{realContractStartTime} AND info.real_contract_end_time >= #{realContractEndTime}

ORDER BY id DESC

    limit #{offset},#{length}
