mapper.select:
SELECT p.*, d.id AS dictId, d.value AS projectType FROM project p LEFT JOIN dict d ON p.dict_id = d.id WHERE 1=1
AND p.name LIKE CONCAT('%', #{projectName}, '%')
AND p.code LIKE CONCAT('%', #{code}, '%')
AND d.id = #{dictId}
AND p.status = #{status}
ORDER BY p.id DESC
limit #{offset},#{length}
