mapper.select:
    SELECT
      s.*,d1.value as taxpayerTypeStr,d2.value as typeStr,d3.value as professionStr
    FROM
      subcontractor s
    LEFT JOIN
      dict d1 ON s.taxpayer_type_id = d1.id
    LEFT JOIN
	  dict d2 on s.type_id = d2.id
    LEFT JOIN
	  dict d3 on s.profession_type = d3.id
	WHERE 1=1

      AND s.name LIKE concat('%',#{name},'%')


      AND s.type_id = #{typeId}


      AND s.profession_type = #{professionType}


      AND s.registered_capital <= #{maxAmount} AND s.registered_capital >= #{minAmount}


      AND s.share_evaluation = #{shareEvaluation}


      AND s.group_evaluation = #{groupEvaluation}


      AND s.company_evaluation = #{companyEvaluation}

    ORDER BY s.id DESC

      limit #{offset},#{length}
