mapper.select:
SELECT sr.*, la.team_name AS teamName, p.name AS projectName, s.name AS subcontractorName, la.contract_person AS contractPerson, la.phone, la.estimated_contract_amount AS contractAmount, la.settlement_amount AS settlementAmount FROM subcontractor_resume sr LEFT JOIN labor_account la ON sr.labor_account_id = la.id LEFT JOIN subcontractor s ON sr.subcontractor_id = s.id LEFT JOIN project p ON sr.project_id = p.id WHERE 1=1
AND s.name LIKE concat('%',#{subcontractorName},'%')
AND sr.company_evaluation LIKE concat('%',#{companyEvaluation},'%')
AND sr.construction_scale = #{constructionScale}
ORDER BY id DESC
limit #{offset},#{length}
